<?php

namespace Aiqa\Generator\Entity;

use Aiqa\Generator\RandomDataGeneratorInterface;

class AvatarGenerator implements RandomDataGeneratorInterface
{
    private $fileTypes = ['pdf', 'jpeg', 'jpg'];

    // @codingStandardsIgnoreStart
    private $base64Img = 'data:image/png;base64,';

    public function getRandom()
    {
        $result = [];
        $result['size'] = rand(1, 1000000);
        $result['type'] = $this->fileTypes[0];
        $result['data'] = $this->base64Img;

        return $result;
    }
}
